mapper.update:
UPDATE t_order SET
 order_status=#{status},
 refund_fee=#{refundFee},
 update_date=NOW(),
 remark=#{remark}
 WHERE id=#{orderId}

UPDATE t_order SET
update_date=now()
,order_status=#{orderStatus}
,wx_order_no=#{wxOrderNo}
,order_no=#{orderNo}
,pay_date=#{payDate}
WHERE id=#{orderId}

UPDATE t_order SET
update_date=now(),
delivery_date=now()
,order_status=#{orderStatus}
,send_good_id=#{sendGoodId}
,delivery_no=#{deliveryNo}
WHERE id=#{id}
