mapper.select:
SELECT tor.id, tor.userId, tor.orderNo, tor.goodId, tor.goodSnapShotId, tor.deliveryId, tor.orderStatus, tor.orderType, tor.payType, tor.message, tor.goodName, tor.count, tor.payDate, tor.sendGoodId, tor.useTicket, tor.ticketId, tor.remark, tor.orderFee, tor.reduceFee, tor.paymentFee, tor.goodFee, tor.deliveryNo, tor.operatePoint, tor.freightFee, tg.mainImgUrl as goodMainImage, tsg.expressName as expressName FROM t_order tor LEFT JOIN t_good_snapshot tg ON tor.goodSnapShotId=tg.id LEFT JOIN t_send_good tsg ON tor.id=tsg.orderId WHERE tor.userId=#{userId}
AND tor.orderStatus=#{orderStatus}
GROUP BY tor.id
