mapper.select:
SELECT tor.id, tor.user_id, tor.order_no, tor.good_id, tor.good_snapshot_id, tor.delivery_id, tor.order_status, tor.order_type, tor.pay_type, tor.message, tor.good_name, tor.count, tor.pay_date, tor.send_good_id, tor.use_ticket, tor.ticket_id, tor.remark, tor.order_fee, tor.reduce_fee, tor.payment_fee, tor.good_fee, tor.use_point tor.delivery_no, tor.operate_point, tor.freight_fee, tg.main_image_url as goodMainImage, tsg.express_name as expressName FROM t_order tor LEFT JOIN t_good_snapshot tg ON tor.good_snapshot_id=tg.id LEFT JOIN t_send_good tsg ON tor.id=tsg.order_id WHERE tor.user_id=#{userId}
AND tor.order_status=#{orderStatus}
GROUP BY tor.id
